export const getListItemClickHandler = ({ list, multiple, }) => {
    const onItemClick = ({ id }) => {
        if (list.state.disabledById[id])
            return;
        // always activate selected item
        list.state.setActiveItemId(id);
        if (list.state.expandedById && id in list.state.expandedById && list.state.setExpanded) {
            list.state.setExpanded((prevState) => (Object.assign(Object.assign({}, prevState), { [id]: !prevState[id] })));
        }
        else {
            list.state.setSelected((prevState) => (Object.assign(Object.assign({}, (multiple ? prevState : {})), { [id]: multiple ? !prevState[id] : true })));
        }
    };
    return onItemClick;
};
